/*
function Card(props) {

  function handleClick() {
    props.onCardClick(props.card);
  }

  return (
    <li className="element">
      <figure className="element__figure">
        <img onClick={handleClick} className="element__image" src={props.card.link} alt={props.card.name} />
        <figcaption className="element__caption">
          <h2 className="element__heading">{props.card.name}</h2>
          <div className="element__like-container">
            <button className="element__like" type="button"></button>
            <div className="element__like-number">{props.card.likes.length}</div>
          </div>
        </figcaption>
      </figure>
      <button className="element__delete" type="button"></button>
    </li>
  );
}

export default Card;
 */

function Card(props) {

    function handleClick() {
        props.onCardClick(props.card);
    }

    return (
        <template className="template">
            <div className="card">
                <img className="card__image" src={props.card.link} alt={props.card.name} />
                <button class="card__delete" type="button"></button>
                <div class="card__group">
                    <h3 class="card__text"></h3>
                    <div class="card__like-container">
                        <button class="card__like" type="button"></button>
                        <span class="card__like-counter"></span>
                    </div>
                </div>
            </div>
        </template>
    );
}
