import React from 'react'
import Card from './Card.js'
import api from '../utils/api.js'

function Main(props) {
    const [userName, setUserName] = React.useState('');
    const [userAbout, setUserAbout] = React.useState('');
    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {
        api.initData()
            .then((data) => {
                const [initialCards, userInfo] = data;

                setUserName(userInfo.name);
                setUserAbout(userInfo.about);
                setCards(initialCards);
            })
            .catch((err) => {
                console.log(err);
            });
    });

    return (
        <main className="content">
            <section className="profile">
                <div className="profile__avatar-container">
                    <img className="profile__avatar" src="https://w7.pngwing.com/pngs/519/239/png-transparent-rick-sanchez-human-morty-smith-pocket-mortys-rick-sanches-white-mammal-face.png" alt="Фото профиля" />
                    <button className="profile__avatar-button" onClick={props.onEditAvatar}></button>
                </div>
                <div className="info">
                    <h1 className="info__title">{userName}</h1>
                    <p className="info__subtitle">{userAbout}</p>
                    <button className="info__button" type="button" onClick={props.onEditProfile}></button>
                </div>
                <button className="profile__add-button" type="button" onClick={props.onAddPlace}></button>
            </section>
            <section className="cards">
                <div className="card">
                        {cards.map((card) => (
                            <Card card={card} key={card._id} onCardClick={props.onCardClick} />
                        ))}
                </div>
            </section>
        </main>
    );
}
export default Main;
