import React from 'react'
import Card from './Card.js'
import Api from '../utils/Api.js'

function Main(props) {
    const [userName, setUserName] = React.useState('');
    const [userAbout, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');
    const [cards, setCards] = React.useState([]);
    return (
        <main class="content">
            <section class="profile">
                <div class="profile__avatar-container">
                    <img class="profile__avatar" src="https://w7.pngwing.com/pngs/519/239/png-transparent-rick-sanchez-human-morty-smith-pocket-mortys-rick-sanches-white-mammal-face.png" alt="Фото профиля" />
                    <button class="profile__avatar-button"></button>
                </div>
                <div class="info">
                    <h1 class="info__title">Рик Санчез</h1>
                    <p class="info__subtitle">Гениальный и немолодой ученый</p>
                    <button class="info__button" type="button"></button>
                </div>
                <button class="profile__add-button" type="button"></button>
            </section>
            <section class="cards">
            </section>
        </main>
    );
}
export default Main;
